import User from '../models/user';
import dotenv from "dotenv";
import jwt from 'jsonwebtoken';
import expressJwt from 'express-jwt';
import { check, validationResult } from 'express-validator'
dotenv.config();

export const userValidationResult = (req, res, next) => {
    const result = validationResult(req)
    if (!result.isEmpty()) {
        const error = result.array()[0].msg
        return res.status(422).json({
            success: false,
            error: error
        })
    }
    next();
}
//validate k t 
export const Validate = [
    check('name').trim().not().isEmpty().withMessage("Name is required").isLength({ min: 3, max: 30 })
        .withMessage("Tn phi di t 3 n 30 k t !"),

    check('email').trim().not().isEmpty().withMessage("Email is required")
        .matches('^[A-Za-z0-9]{6,32}@([a-zA-Z0-9]{2,12})(.[a-zA-Z]{2,12})+$')
        .withMessage("Email khng ng nh dng. Email cha k t ch ci v s !!"),

    // check('phone').trim().not().isEmpty().withMessage("Phone is required")
    //     .matches('/((09|03|07|08|05)+([0-9]{8})\b)/g')
    //     .withMessage("S in thoi khng ng nh dng hoc khng ng u s ca VN !!"),

    check('password').trim().not().isEmpty().withMessage('Password is required').isLength({ min: 8 })
        .withMessage('Mt khu phi nhp di t nht 8 k t !')
]

//check mail tn ti
export const checkMail = (req, res, next) => {
    const email = req.body;
    User.findOne(email).exec((err, data) => {
        if (err || data) {
            return res.status(400).json({
                status: false,
                error: "Email  tn ti. Vui lng ng k gmail khc !!"
            })
        }
        next();
    })
}
// Register
export const register = (req, res) => {
    const { name, email, image, password, phone, birth } = req.body;
    if (!name || !email || !image || !password || !phone || !birth) {
        return res.status(400).json({
            status: false,
            error: "Bn cn nhp y  thng tin"
        })
    } else {
        const user = new User(req.body);
        user.save((err, user) => {
            if (err || !user) {
                return res.status(400).json({
                    error: "Khng th ng k ti khon",
                })
            }
            return res.status(200).json({
                status: true,
                message: "ng k ti khon thnh cng",
                user
            })
        })
    }
}


// Signin
export const signin = (req, res) => {
    const { _email, _password } = req.body;

    if (_email && _password) {
        User.findOne({ email: _email }, (error, user) => {
            if (error || !user) {
                return res.status(400).json({
                    err: "Ti khon khng tn ti ! Vui lng ng k "
                });
            };
            if (!user.authenticate(_password)) {
                return res.status(401).json({
                    error: "Email hoc mt khu khng ng !!",
                });
            }

            const token = jwt.sign({ _id: user.id, role: user.role }, process.env.JWT_SECRET);
            res.cookie('tokenAccess', token, { expire: new Data() + 9999 });
            const { _id, name, email, role } = user;
            return res.json({
                token, id: _id, name: name, email: email, role: role
            });
        });
    } else {
        const { email, password } = req.body;
        User.findOne({ email }, (error, user) => {
            if (error || !user) {
                return res.status(400).json({
                    error: "User with that email does not exit. Please signup"
                });
            }
            if (!user.authenticate(password)) {
                return res.status(400).json({
                    error: "Email and password not match"
                });
            }
            const token = jwt.sign({ _id: user._id, role: user.role }, process.env.JWT_SECRET);
            const { _id, name, email, role, products } = user;
            return res.json({
                token,
                user: { _id, email, name, role, products }
            });
        });
    }
};

//signout
export const signout = (req, res, next) => {
    res.clearCookie("tokenAccess");
    res.json({
        error: "ng xut thnh cng",
    });
};

//kim tra ng nhp
export const requireSignin = expressJwt({
    secret: process.env.JWT_SECRET,
    algorithms: ['HS256'],
    userProperty: 'auth'
})

//kim t xem c phi l ngi dng ca trang web
export const isAuth = (req, res, next) => {
    //start kim tra _id  payload token c trng vi _id trong req.profile
    let user = req.profile && req.auth && req.profile._id == req.auth._id;
    if (!user) {
        return res.status(403).json({
            error: "Khng truy cp !"
        })
    }
    next();
};

// kim tra xem c phi admin (role == 0)
export const isAdmin = (req, res, next) => {
    if(req.auth.role != 0 ) {
        return res.status(400).json({
            error : "Bn khng phi admin. Vui lng tr li trang ch "
        });
    }
    next();
}

//kim tra c phi l admin trong router products
export const checkAdmin = (req, res, next) => {
    if (req.auth.role != 0 ) {
        return res.status(403).json({
            err : "Bn khng phi admin. Vui lng tr li trang ch "
        })
    }
    next();
}

