import Book from "../models/book";
import _ from "lodash";
import { fromidable } from 'formidable';
import fs from 'fs';

export const addBook = (req, res) => {
    const { bookName, price, avatar, description, status, author } = req.body;
    if (!bookName || !price || !avatar || !description || !status || !author) {
        return res.status(401).json({
            status: false,
            error: "Bn cn nhp y  thng tin sch !!"
        })
    } else {
        const book = new Book(req.body);
        book.save((err, data) => {
            if (err) {
                res.status(402).json({
                    err: "Khng th thm thng tin sch !!"
                })
            }
            return res.json({
                message: "Thm thng tin sch thnh cng ",
                data
            })
        })
    }
}

export const listBook = (req, res) => {
    Book.find((err, data) => {
        if (err || !data) {
            return res.status(400).json({
                err: "Khng th hin th c danh sch"
            })
        }
        return res.json({
            message : "Ly dnh sch ca sch thnh cng ",
            data
        })
    })
}

export const bookById = (req, res, next , id) => {
    Book.findById(id).exec((err, book) => {
        if(err) {
            res.status(400).json({
                err : "Khng tm thy sch"
            })
        }
        req.book = book;
        next();
    })
}

export const detailBook = (req, res) => {
    return res.json(req.book)
}

export const updateBook = (req, res) => {
    let book = req.book
    book = _.assignIn(book, req.body);

    book.save((err, book) => {
        if(err) {
            return res.status(401).json({
                err : "Error !!"
            })
        }
        return res.json({
            message  :"Cp nht sch thnh cng",
            book
        })
    })
}

export const removeBook = (req, res) => {
    let book = req.book;
    book.remove((err, data) => {
        if(err) {
            return res.status(400).json({
                status: false,
                err : "Khng th xo c sn phm"
            });
        };
        return res.json({
            message : "Xo thnh cng sch",
            data
        })
    })
}

