import Categories from "../models/category";
import _ from 'lodash';

// Add Category book
export const addCategories = (req, res) => {
    const category = new Categories(req.body);
    category.save((err, data) => {
        if (err) {
            return res.status(401).json({
                err: "Khng thm c danh mc"
            })
        } else {
            return res.json({
                status: true,
                message: "Thm danh mc thnh cng",
                data
            })
        }
    })
}

// show list categories
export const showListCate = (req, res, next) => {
    Categories.find({}).then((categories) => {
        categories.map((categories) => categories.toObject());
        return res.json({
            message: "Ly danh mc thnh cng",
            categories
        });
    }).catch(next);
}

// Ly id
export const categoryId = (req, res, next, id) => {
    Categories.findById(id).exec((err, category) => {
        if (err || !category) {
            return res.status(401).json({
                err: "Khng ly c sn phm !"
            })
        }
        req.category = category;
        next();
    });
}

//update category
export const updateCategories = (req, res) => {
    const category = _.assignIn(req.category, req.body);
    category.save((err, db) => {
        if (err) {
            res.status(403).json({
                err: "Khng th cp nht danh mc !!",
            })
        } else {
            return res.status(200).json({
                message: "Cp nht danh mc sch thnh cng",
                db
            })
        }
    })
}

//Detail cate
export const cateDetail = (req, res) => {
    return res.json(req.category)
}

//Delete category
export const removeCategories = (req, res, next) => {
    let category = req.body;
    category.remove((err, data) => {
        if(err || !data) {
            return res.status(401).json({
                error : "Khng th xo danh mc !!"
            })
        }else {
            return res.status(200).json({
                message : "Xo thnh cng danh mc",
                data
            })
        }
    })
}

