        type: String,
        required: true,
    },
    status: {
        required: true,
        type: String
    },
    author: {
        type: String,
        required: true
    },
    discount: {
        type: Number
    }
}, { timestamps: true } 

)
module.exports = mongoose.model("Book",bookSchema)

